mapper.select:
select *
from member
where email = #{email} and pw = #{pw}

mapper.update:
UPDATE Member
SET profile = #{profile}
WHERE seq_member = #{seqMember}

UPDATE Member
SET nickname = #{nickname}
WHERE seq_member = #{seqMember}

UPDATE Member
SET PW = #{password}
WHERE seq_member = #{seqMember}

mapper.delete:
UPDATE Member
SET status = 0
WHERE seq_member = #{seqMember}
